mapper.select:
select h.*, u.UserName,u.MoneyCard, m.SerialNo,m.Year,m.Month,m.State,m.SavePath,m.FileName,m.Title,m.Content, s.StationName,s.StationCode,d.DepartmentCode,d.DepartmentName, r.RoleCode,r.RoleName from scorehistory h, user u ,monthsummary m,station s,department d,role r,user_role ur
and u.UserName like concat('%',#{username},'%')
and u.StationCode=#{stationcode}
and h.Year=#{year}
and h.Month=#{month}
and m.State=#{state}
and (m.Title is null or m.Title='') and (m.Content is null or m.Content='')
and h.ScoreStatus=#{scorestatus}
and u.UserCode=h.UserCode and u.UserCode =m.EmployeeCode and u.RoleType="0" and u.Flag="0" and u.StationCode=s.StationCode and s.DepartmentCode=d.DepartmentCode and u.UserCode=ur.UserCode and r.RoleCode=ur.RoleCode and r.RoleCode in("100","200","300","50") and h.Year=m.Year and m.`Month`=h.`Month`
order by h.Year+0 desc, h.Month+0 desc

select h.*, u.UserCode,u.UserName,u.MoneyCard, m.SerialNo,m.Year,m.Month,m.State,m.ScoreStatus, s.StationName,s.StationCode,d.DepartmentCode,d.DepartmentName, r.RoleCode,r.RoleName from scorehistory h, user u ,monthsummary m,station s,department d,role r,user_role ur
and u.UserName like concat('%',#{username},'%')
and u.StationCode=#{stationcode}
and h.Year=#{year}
and h.Month=#{month}
and m.State=#{state}
and (m.Title is null or m.Title='') and (m.Content is null or m.Content='')
and h.ScoreStatus=#{scorestatus}
and u.UserCode=h.UserCode and u.UserCode =m.EmployeeCode and u.RoleType="0" and u.Flag="0" and u.StationCode=s.StationCode and s.DepartmentCode=d.DepartmentCode and u.UserCode=ur.UserCode and r.RoleCode=ur.RoleCode and r.RoleCode = "150" and h.Year=m.Year and m.`Month`=h.`Month`
order by h.Year+0 desc, h.Month+0 desc

select h.*, u.UserCode,u.UserName,u.MoneyCard, m.SerialNo,m.Year,m.Month,m.State,m.ScoreStatus, s.StationName,s.StationCode,d.DepartmentCode,d.DepartmentName, r.RoleCode,r.RoleName from scorehistory h, user u ,monthsummary m,station s,department d,role r,user_role ur
and h.Year=#{year}
and h.Month=#{month}
and h.ScoreStatus !=3 and u.UserCode=h.UserCode and u.UserCode =m.EmployeeCode and u.RoleType="0" and u.Flag="0" and u.StationCode=s.StationCode and s.DepartmentCode=d.DepartmentCode and u.UserCode=ur.UserCode and r.RoleCode=ur.RoleCode and r.RoleCode in("100","150","200","300","50") and h.Year=m.Year and m.`Month`=h.`Month` order by h.Year+ 0 desc,h.Month+0 desc, h.ScoreStatus

select h.*, u.UserCode,u.UserName,u.MoneyCard, m.SerialNo,m.Year,m.Month,m.State,m.ScoreStatus, s.StationName,s.StationCode,d.DepartmentCode,d.DepartmentName, r.RoleCode,r.RoleName from scorehistory h, user u ,monthsummary m,station s,department d,role r,user_role ur
and h.Year=#{year}
and h.Month=#{month}
and u.UserCode=h.UserCode and u.UserCode =m.EmployeeCode and u.RoleType="0" and u.Flag="0" and u.StationCode=s.StationCode and s.DepartmentCode=d.DepartmentCode and u.UserCode=ur.UserCode and r.RoleCode=ur.RoleCode and r.RoleCode in("100","150","200","300","50") and h.Year=m.Year and m.`Month`=h.`Month` order by h.Year+ 0 desc,h.Month+0 desc, h.ScoreStatus
